' => 'motor_model_id', 'type' => 'query', 'method' => 'motor_model_id_filterQuery'),        array('name' => 'company_id', 'type' => 'query', 'method' => 'company_id_filterQuery'),        array('name' => 'motor_id', 'type' => 'query', 'method' => 'motor_id_filterQuery'),        array('name' => 'uniqid', 'type' => 'query', 'method' => 'vendor_uniqid_filterQuery'),    );    public function motor_id_filterQuery($data = array()) {        if (!isset($data['motor_id'])) {            return array();        }        $query = $data['motor_id'];        if ($query) {            return array(                'OR' => array(                    'Taxi.motor_id' => $query                )            );        } else {            return array();        }    }    public function filterQuery($data = array()) {        if (!isset($data['status'])) {            return array();        }        $query = $data['status'];        if ($query != '') {            return array(                'OR' => array(                    'Taxi.status' => $query                )            );        } else {            return array();        }    }    public function verifiedfilterQuery($data = array()) {        if (!isset($data['verified'])) {            return array();        }        $query = $data['verified'];        if ($query != '') {            return array(                'OR' => array(                    'Taxi.verified' => $query                )            );        } else {            return array();        }    }    public function state_filterQuery($data = array()) {        if (!isset($data['state_id'])) {            return array();        }        $query = $data['state_id'];        if ($query) {            return array(                'OR' => array(                    'Taxi.state_id' => $query                )            );        } else {            return array();        }    }    public function city_filterQuery($data = array()) {        if (!isset($data['city_id'])) {            return array();        }        $query = $data['city_id'];        if ($query) {            return array(                'OR' => array(                    'Taxi.city_id' => $query                )            );        } else {            return array();        }    }    public function motor_type_id_filterQuery($data = array()) {        if (!isset($data['motor_type_id'])) {            return array();        }        $query = $data['motor_type_id'];        if ($query) {            return array(                'OR' => array(                    'Taxi.motor_type_id' => $query                )            );        } else {            return array();        }    }    public function motor_model_id_filterQuery($data = array()) {        if (!isset($data['motor_model_id'])) {            return array();        }        $query = $data['motor_model_id'];        if ($query) {            return array(                'OR' => array(                    'Taxi.motor_model_id' => $query                )            );        } else {            return array();        }    }    public function company_id_filterQuery($data = array()) {        if (!isset($data['company_id'])) {            return array();        }        $query = $data['company_id'];        if ($query) {            return array(                'OR' => array(                    'Taxi.company_id' => $query                )            );        } else {            return array();        }    }    public function vendor_uniqid_filterQuery($data = array()) {        if (!isset($data['uniqid'])) {            return array();        }        $query = $data['uniqid'];        if ($query) {            return array(                'OR' => array(                    'Taxi.uniqid LIKE' => "%" . $query . "%"                )            );        } else {            return array();        }    }    public $virtualFields = array(        "motor_type" => "select name from motor_types where id=Taxi.motor_type_id",        "capacity" => "select capacity from motor_types where id=Taxi.motor_type_id",        "motor" => "select name from motors where id=Taxi.motor_id",        "motor_model" => "select name from motor_models where id=Taxi.motor_model_id",        "service_category" => "select name from service_categories where id=Taxi.service_category_id",        "company_type" => "select user_role_id from users where id=Company.user_id",        "uniqid" => "select uniqid from users where id=Company.user_id",        "firstname" => "select firstname from users where id=Company.user_id",        "lastname" => "select lastname from users where id=Company.user_id",            //"cab_status" => "select cab_status from cab_attachment_reports where taxi_id=Taxi.id"    );    public $validate = array(        'name' => array(            'valid' => array(                'rule' => 'notEmpty',                'required' => true,                'allowEmpty' => false,                'message' => 'Please enter a value for Taxi .'            )        )    );}?>