<?php/** * @license MIT License (http://www.opensource.org/licenses/mit-license.php) * * PHP version 5 * CakePHP version 1.3 *//** * MotorTypes Users Controller * * @package MotorTypes * @subpackage users.controllers */class JobCategoriesController extends JobCategoryAppController {    /**     * Controller name     *     * @var string     */    var $name = 'JobCategories';    /**     * Helpers     *     * @var array     */    public $helpers = array('Html', 'Form', 'Session', 'Time', 'Text');    //public $paginate = array('limit'=>5,'order'=>'MotorType.id','page'=>1);    /**     * Components     *     * @var array     */    public $components = array('Auth', 'Session', 'Email', 'Cookie', 'Search.Prg', 'RequestHandler');    public $presetVars = array(        array('field' => 'name', 'type' => 'value')    );    // public $presetVars = 	true;    public function beforeFilter() {        parent::beforeFilter();        $this->set('tab_open', 'sys_masters');        $this->set('model', $this->modelClass);        $this->loadModel('Education');//        $state_list = $this->Education->find('list', array(//            'conditions' => array(//                'is_active' => 1,//            ),//        ));//        $this->set('state_list', $state_list);    }    /**     * Admin Index     *     * @return void     */    function index() {//echo "yo";die;        // Breadcrumb        if (!empty($this->data)) {            $this->Prg->commonProcess();        }        $page = ((isset($this->params->named['page']) && $this->params->named['page'] != "") ? $this->params->named['page'] : 0);        $limitValue = $limit = 50;        $this->set('limitValue', $limitValue);        $this->set('limit', $limit);        $this->{$this->modelClass}->data[$this->modelClass] = $this->passedArgs;        $parsedConditions = $this->{$this->modelClass}->parseCriteria($this->passedArgs);        $this->paginate = array(            'limit' => $limit,            'order' => array($this->modelClass . '.id' => 'desc')        );        //pr($this->paginate());        $this->set('result', $this->paginate());        $this->set('page', $page);        $this->set('title_for_layout', 'Job Categories');    }    function add() {        if (!empty($this->data)) {            $this->{$this->modelClass}->set($this->data);            //if ($this->{$this->modelClass}->validateAdd()) {            //pr($this->data);exit;            if ($this->data['LanguageMapJobCategory'][1]['title']) {                $upload_folder = CATEGORY_WBROOT_PATH_ICONS;                $uploadImgArray = $this->data['JobCategory']['icon'];                $file_name = '';                if (isset($uploadImgArray) && $uploadImgArray['name'] != "") {                    $file_name = basename($uploadImgArray['name']);                    $imgExtension = pathinfo($file_name, PATHINFO_EXTENSION);                    $image_name = explode("." . $imgExtension, $file_name);                    $prefix = time();                    $file_name = base64_encode($prefix . $image_name[0]) . "." . $imgExtension;                    if (move_uploaded_file($uploadImgArray['tmp_name'], $upload_folder . DS . $file_name)) {                                            }                }                $datas = array(                    'title' => $this->data['LanguageMapJobCategory'][1]['title'],                   // 'education_id' => $this->data['JobCategory']['education_id'],                    'is_active' => 1,                    'icon' => $file_name,                    'created_on' => date("Y-m-d H:i:s"),                    'created_by' => $this->Auth->user('id'),                );                if ($this->{$this->modelClass}->save($datas)) {                    $this->loadModel('LanguageMapJobCategory');                    $latest_id = $this->{$this->modelClass}->id;                    $languageStates = $this->data['LanguageMapJobCategory'];                    $i = 0;                    foreach ($languageStates as $lang_id => $ldata) {                        $datas2 = array();                        $this->LanguageMapJobCategory->create();                        $datas2['LanguageMapJobCategory']['title'] = $ldata['title'];                        $datas2['LanguageMapJobCategory']['job_category_id'] = $latest_id;                        $datas2['LanguageMapJobCategory']['language_id'] = $lang_id;                        $i++;                        $this->LanguageMapJobCategory->save($datas2);                    }                    $this->Session->setFlash('Job Category has been added.', 'success');                    $this->redirect(array('action' => 'index'));                }            } else {                $this->Session->setFlash('Enter in English.', 'error');                $this->redirect(array('action' => 'add'));            }            //}        }        $this->set('title_for_layout', 'Add New Job Category');    }    function edit($id = null) {        $this->set('id', $id);        $user = $this->{$this->modelClass}->findById($id);        $lanArr = array();        if (!empty($user)) {            $statl = $user['LanguageMapJobCategory'];            foreach ($statl as $new) {                $lanArr[$new['language_id']] = $new;            }        }        $user['LanguageMapJobCategory'] = $lanArr;        $this->{$this->modelClass}->set($user);        $this->set('record', $user);        //pr($user);        if (empty($this->data)) {            $this->data = $user;        } else {            $this->{$this->modelClass}->set($this->data);            //if ($this->{$this->modelClass}->validateUpdate()) {            if (!empty($this->data['LanguageMapJobCategory']) && $this->data['LanguageMapJobCategory'][1]['title']) {                                $upload_folder = CATEGORY_WBROOT_PATH_ICONS;                $uploadImgArray = $this->data['JobCategory']['icon'];                $file_name = $user['JobCategory']['icon'];                if (isset($uploadImgArray) && $uploadImgArray['name'] != "") {                    $file_name = basename($uploadImgArray['name']);                    $imgExtension = pathinfo($file_name, PATHINFO_EXTENSION);                    $image_name = explode("." . $imgExtension, $file_name);                    $prefix = time();                    $file_name = base64_encode($prefix . $image_name[0]) . "." . $imgExtension;                    if (move_uploaded_file($uploadImgArray['tmp_name'], $upload_folder . DS . $file_name)) {                                            }                }                                $datas = array(                    'title' => $this->data['LanguageMapJobCategory'][1]['title'],                    'modified_on' => date("Y-m-d H:i:s"),                    'modified_by' => $this->Auth->user('id'),                    //'education_id' => $this->data['JobCategory']['education_id'],                    'icon' => $file_name,                );                if ($this->{$this->modelClass}->save($datas)) {                    $this->loadModel('LanguageMapJobCategory');                    $latest_id = $id;                    $this->LanguageMapJobCategory->deleteAll(array("LanguageMapJobCategory.job_category_id" => $latest_id));                    $languageStates = $this->data['LanguageMapJobCategory'];                    $i = 0;                    foreach ($languageStates as $lang_id => $ldata) {                        $datas2 = array();                        $this->LanguageMapJobCategory->create();                        $datas2['LanguageMapJobCategory']['title'] = $ldata['title'];                        $datas2['LanguageMapJobCategory']['job_category_id'] = $latest_id;                        $datas2['LanguageMapJobCategory']['language_id'] = $lang_id;                        $i++;                        $this->LanguageMapJobCategory->save($datas2);                    }                    $this->Session->setFlash('Job Category has been updated.', 'success');                    $this->redirect(array('action' => 'index'));                }            }            //}        }        $this->set('title_for_layout', 'Update Job Category');    }    public function change_status() {        if ($this->request->is('Ajax')) {            if ($this->data['id'] != null) {                $data['is_active'] = $this->data['status'] == 1 ? false : true;                $this->{$this->modelClass}->id = $this->data['id'];                $this->{$this->modelClass}->save($data, false);                echo json_encode(array('succ' => 1, 'msg' => 'Status has been changed.'));                die;            }        }        exit;    }}