<?php/** * @license MIT License (http://www.opensource.org/licenses/mit-license.php) * * PHP version 5 * CakePHP version 1.3 *//** * MotorTypes Users Controller * * @package MotorTypes * @subpackage users.controllers */class PanchayatsController extends PanchayatAppController {    /**     * Controller name     *     * @var string     */    var $name = 'Panchayats';    /**     * Helpers     *     * @var array     */    public $helpers = array('Html', 'Form', 'Session', 'Time', 'Text');    //public $paginate = array('limit'=>5,'order'=>'MotorType.id','page'=>1);    /**     * Components     *     * @var array     */    public $components = array('Auth', 'Session', 'Email', 'Cookie', 'Search.Prg', 'RequestHandler');    public $presetVars = array(        array('field' => 'name', 'type' => 'value')    );    // public $presetVars = 	true;    public function beforeFilter() {        parent::beforeFilter();        $this->set('tab_open', 'sys_masters');        $this->set('model', $this->modelClass);        $this->loadModel('State');        $state_list = $this->State->find('list', array(            'conditions' => array(                'is_active' => 1,            ),        ));        $this->set('state_list', $state_list);                $this->loadModel('District');        $district_list = $this->District->find('list', array(            'conditions' => array(                'is_active' => 1,            ),        ));        $this->set('district_list', $district_list);                        $this->loadModel('Block');        $block_list = $this->Block->find('list', array(            'conditions' => array(                'is_active' => 1,            ),        ));        $this->set('block_list', $block_list);            }    /**     * Admin Index     *     * @return void     */    function index() {//echo "yo";die;        // Breadcrumb        if (!empty($this->data)) {            $this->Prg->commonProcess();        }        $page = ((isset($this->params->named['page']) && $this->params->named['page'] != "") ? $this->params->named['page'] : 0);        $limitValue = $limit = 50;        $this->set('limitValue', $limitValue);        $this->set('limit', $limit);        $this->{$this->modelClass}->data[$this->modelClass] = $this->passedArgs;        $parsedConditions = $this->{$this->modelClass}->parseCriteria($this->passedArgs);        if (!empty($this->request->query)) {            if (isset($this->request->query['state_id']) && $this->request->query['state_id'] != "") {                array_push($parsedConditions, array('Panchayat.state_id' => $this->request->query['state_id']));                $this->set("state_id", $this->request->query['state_id']);            }            if (isset($this->request->query['district_id']) && $this->request->query['district_id'] != "") {                array_push($parsedConditions, array('Panchayat.district_id' => $this->request->query['district_id']));                $this->set("district_id", $this->request->query['district_id']);            }            if (isset($this->request->query['block_id']) && $this->request->query['block_id'] != "") {                array_push($parsedConditions, array('Panchayat.block_id' => $this->request->query['block_id']));                $this->set("block_id", $this->request->query['block_id']);            }        }        $this->paginate = array(            'conditions' => array($parsedConditions),            'limit' => $limit,            'order' => array($this->modelClass . '.id' => 'desc')        );        //pr($this->paginate());        $this->set('result', $this->paginate());        $this->set('page', $page);        $this->set('title_for_layout', 'Panchayats');    }    function add() {        if (!empty($this->data)) {            $this->{$this->modelClass}->set($this->data);            //if ($this->{$this->modelClass}->validateAdd()) {            //pr($this->data);exit;            if ($this->data['LanguageMapPanchayat'][1]['name']) {                $datas = array(                    'name' => $this->data['LanguageMapPanchayat'][1]['name'],                    'state_id' => $this->data['Panchayat']['state_id'],                    'district_id' => $this->data['Panchayat']['district_id'],                    'block_id' => $this->data['Panchayat']['block_id'],                    'is_active' => 1,                    'created_on' => date("Y-m-d H:i:s"),                    'created_by' => $this->Auth->user('id'),                );                if ($this->{$this->modelClass}->save($datas)) {                    $this->loadModel('LanguageMapPanchayat');                    $latest_id = $this->{$this->modelClass}->id;                    $languageStates = $this->data['LanguageMapPanchayat'];                    $i = 0;                    foreach ($languageStates as $lang_id => $ldata) {                        $datas2 = array();                        $this->LanguageMapPanchayat->create();                        $datas2['LanguageMapPanchayat']['name'] = $ldata['name'];                        $datas2['LanguageMapPanchayat']['panchayat_id'] = $latest_id;                        $datas2['LanguageMapPanchayat']['language_id'] = $lang_id;                        $i++;                        $this->LanguageMapPanchayat->save($datas2);                    }                    $this->Session->setFlash('Panchayat has been added.', 'success');                    $this->redirect(array('action' => 'index'));                }            } else {                $this->Session->setFlash('Enter in English.', 'error');                $this->redirect(array('action' => 'add'));            }            //}        }        $this->set('title_for_layout', 'Add New Panchayat');    }    function edit($id = null) {        $this->set('id', $id);        $user = $this->{$this->modelClass}->findById($id);        $lanArr = array();        if (!empty($user)) {            $statl = $user['LanguageMapPanchayat'];            foreach ($statl as $new) {                $lanArr[$new['language_id']] = $new;            }        }        $user['LanguageMapPanchayat'] = $lanArr;        $this->{$this->modelClass}->set($user);        $this->set('record', $user);        //pr($user);        if (empty($this->data)) {            $this->data = $user;        } else {            $this->{$this->modelClass}->set($this->data);            //if ($this->{$this->modelClass}->validateUpdate()) {            if (!empty($this->data['LanguageMapPanchayat']) && $this->data['LanguageMapPanchayat'][1]['name']) {                $datas = array(                    'name' => $this->data['LanguageMapPanchayat'][1]['name'],                    'modified_on' => date("Y-m-d H:i:s"),                    'modified_by' => $this->Auth->user('id'),                );                if ($this->{$this->modelClass}->save($datas)) {                    $this->loadModel('LanguageMapPanchayat');                    $latest_id = $id;                    $this->LanguageMapPanchayat->deleteAll(array("LanguageMapPanchayat.panchayat_id" => $latest_id));                    $languageStates = $this->data['LanguageMapPanchayat'];                    $i = 0;                    foreach ($languageStates as $lang_id => $ldata) {                        $datas2 = array();                        $this->LanguageMapPanchayat->create();                        $datas2['LanguageMapPanchayat']['name'] = $ldata['name'];                        $datas2['LanguageMapPanchayat']['panchayat_id'] = $latest_id;                        $datas2['LanguageMapPanchayat']['language_id'] = $lang_id;                        $i++;                        $this->LanguageMapPanchayat->save($datas2);                    }                    $this->Session->setFlash('Panchayat has been updated.', 'success');                    $this->redirect(array('action' => 'index'));                }            }            //}        }        $this->set('title_for_layout', 'Update Panchayat');    }    public function change_status() {        if ($this->request->is('Ajax')) {            if ($this->data['id'] != null) {                $data['is_active'] = $this->data['status'] == 1 ? false : true;                $this->{$this->modelClass}->id = $this->data['id'];                $this->{$this->modelClass}->save($data, false);                echo json_encode(array('succ' => 1, 'msg' => 'Status has been changed.'));                die;            }        }        exit;    }            public function get_district_list() {        $this->autoLayout = false;        $this->autoRender = false;        $state_id = $this->request->data('state_id');        $district_id = @$this->request->data('district_id');        if ($this->request->is('ajax')) {            $this->loadModel('District');            $states_list = $this->District->find('all', array(                'conditions' => array(                    'District.state_id' => $state_id                ),                'fields' => array(                    'District.id', 'District.name'                ),                'recursive' => '-1'            ));            $html = "<option value=''>Select</option>";            foreach ($states_list as $state) {                $selected = "";                if ($district_id) {                    if ($district_id == $state['District']['id']) {                        $selected = 'selected="selected"';                    }                }                $html .= "<option " . $selected . " value='" . $state['District']['id'] . "'>" . $state['District']['name'] . "</option>";            }            echo $html;        }    }            public function get_block_list() {        $this->autoLayout = false;        $this->autoRender = false;        $district_id = $this->request->data('district_id');        $block_id = @$this->request->data('block_id');        if ($this->request->is('ajax')) {            $this->loadModel('Block');            $states_list = $this->Block->find('all', array(                'conditions' => array(                    'Block.district_id' => $district_id                ),                'fields' => array(                    'Block.id', 'Block.name'                ),                'recursive' => '-1'            ));            $html = "<option value=''>Select</option>";            foreach ($states_list as $state) {                $selected = "";                if ($block_id) {                    if ($block_id == $state['Block']['id']) {                        $selected = 'selected="selected"';                    }                }                $html .= "<option " . $selected . " value='" . $state['Block']['id'] . "'>" . $state['Block']['name'] . "</option>";            }            echo $html;        }    }}