<?php/** * @license MIT License (http://www.opensource.org/licenses/mit-license.php) * * PHP version 5 * CakePHP version 1.3 *//** * MotorTypes Users Controller * * @package MotorTypes * @subpackage users.controllers */class SalariesController extends SalaryAppController {    /**     * Controller name     *     * @var string     */    var $name = 'Salaries';    /**     * Helpers     *     * @var array     */    public $helpers = array('Html', 'Form', 'Session', 'Time', 'Text');    //public $paginate = array('limit'=>5,'order'=>'MotorType.id','page'=>1);    /**     * Components     *     * @var array     */    public $components = array('Auth', 'Session', 'Email', 'Cookie', 'Search.Prg', 'RequestHandler');    public $presetVars = array(        array('field' => 'name', 'type' => 'value')    );    // public $presetVars = 	true;    public $range_types = array(        1 => "Minimum & Maximum",        2 => "Minimum & Above"    );    public function beforeFilter() {        parent::beforeFilter();        $this->set('tab_open', 'sys_masters');        $this->set('range_types', $this->range_types);        $this->set('model', $this->modelClass);    }    /**     * Admin Index     *     * @return void     */    function index() {//echo "yo";die;        // Breadcrumb        if (!empty($this->data)) {            $this->Prg->commonProcess();        }        $page = ((isset($this->params->named['page']) && $this->params->named['page'] != "") ? $this->params->named['page'] : 0);        $limitValue = $limit = 50;        $this->set('limitValue', $limitValue);        $this->set('limit', $limit);        $this->{$this->modelClass}->data[$this->modelClass] = $this->passedArgs;        $parsedConditions = $this->{$this->modelClass}->parseCriteria($this->passedArgs);        $this->paginate = array(            'limit' => $limit,            'order' => array($this->modelClass . '.id' => 'desc')        );        //pr($this->paginate());        $this->set('result', $this->paginate());        $this->set('page', $page);        $this->set('title_for_layout', 'Salaries');    }    function add() {        if (!empty($this->data)) {            $this->{$this->modelClass}->set($this->data);            //if ($this->{$this->modelClass}->validateAdd()) {            //pr($this->data);exit;            if (!empty($this->data['LanguageMapSalaries']) && $this->data['LanguageMapSalaries'][1]['name']) {                $datas = array(                    'name' => $this->data['LanguageMapSalaries'][1]['name'],                    'min_range' => $this->data['Salary']['min_range'],                    'max_range' => $this->data['Salary']['range_type'] == 1 ? $this->data['Salary']['max_range'] : 10000000,                    'range_type' => $this->data['Salary']['range_type'],                    'is_active' => 1,                    'created_on' => date("Y-m-d H:i:s"),                    'created_by' => $this->Auth->user('id'),                );                if ($this->{$this->modelClass}->save($datas)) {                    $this->loadModel('LanguageMapSalaries');                    $latest_id = $this->{$this->modelClass}->id;                    $languageSalarys = $this->data['LanguageMapSalaries'];                    $i = 0;                    $datas2 = array();                    foreach ($languageSalarys as $lang_id => $ldata) {                        $datas2['LanguageMapSalaries'][$i]['name'] = $ldata['name'];                        $datas2['LanguageMapSalaries'][$i]['salary_id'] = $latest_id;                        $datas2['LanguageMapSalaries'][$i]['language_id'] = $lang_id;                        $i++;                    }                    if (!empty($datas2)) {                        $this->LanguageMapSalaries->saveAll($datas2['LanguageMapSalaries']);                    }                    $this->Session->setFlash('Salary has been added.', 'success');                    $this->redirect(array('action' => 'index'));                }            } else {                $this->Session->setFlash('Enter in English.', 'error');                $this->redirect(array('action' => 'add'));            }            //}        }        $this->set('title_for_layout', 'Add New Salary');    }    function edit($id = null) {        $this->set('id', $id);        $user = $this->{$this->modelClass}->findById($id);        $lanArr = array();        if (!empty($user)) {            $statl = $user['LanguageMapSalaries'];            foreach ($statl as $new) {                $lanArr[$new['language_id']] = $new;            }        }        $user['LanguageMapSalaries'] = $lanArr;        $this->{$this->modelClass}->set($user);        $this->set('record', $user);        //pr($user);        if (empty($this->data)) {            $this->data = $user;        } else {            $this->{$this->modelClass}->set($this->data);            //if ($this->{$this->modelClass}->validateUpdate()) {            if (!empty($this->data['LanguageMapSalaries']) && $this->data['LanguageMapSalaries'][1]['name']) {                $datas = array(                    'name' => $this->data['LanguageMapSalaries'][1]['name'],                    'min_range' => $this->data['Salary']['min_range'],                    'max_range' => $this->data['Salary']['range_type'] == 1 ? $this->data['Salary']['max_range'] : 6000,                    'range_type' => $this->data['Salary']['range_type'],                    'modified_on' => date("Y-m-d H:i:s"),                    'modified_by' => $this->Auth->user('id'),                );                if ($this->{$this->modelClass}->save($datas)) {                    $this->loadModel('LanguageMapSalaries');                    $latest_id = $id;                    $this->LanguageMapSalaries->deleteAll(array("LanguageMapSalaries.salary_id" => $latest_id));                    $languageSalarys = $this->data['LanguageMapSalaries'];                    $i = 0;                    $datas2 = array();                    foreach ($languageSalarys as $lang_id => $ldata) {                        $datas2['LanguageMapSalaries'][$i]['name'] = $ldata['name'];                        $datas2['LanguageMapSalaries'][$i]['salary_id'] = $latest_id;                        $datas2['LanguageMapSalaries'][$i]['language_id'] = $lang_id;                        $i++;                    }                    $this->LanguageMapSalaries->saveAll($datas2['LanguageMapSalaries']);                    $this->Session->setFlash('Salary has been updated.', 'success');                    $this->redirect(array('action' => 'index'));                }            }            // }        }        $this->set('title_for_layout', 'Update Salary');    }    public function change_status() {        if ($this->request->is('Ajax')) {            if ($this->data['id'] != null) {                $data['is_active'] = $this->data['status'] == 1 ? false : true;                $this->{$this->modelClass}->id = $this->data['id'];                $this->{$this->modelClass}->save($data, false);                echo json_encode(array('succ' => 1, 'msg' => 'Status has been changed.'));                die;            }        }        exit;    }}